import { viewContentState } from './atoms';
import { selector } from "recoil";
import axios from 'axios';

export const viewContentSelector = selector({
    key: "viewContentSelector",
    get: ({get}) => {
        return viewContentState;
    },
    set: ({set}) => {
        axios.get("http://localhost:8000/list")
        .then((res)=>{
          set(viewContentState);
        })

    }
})
