import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { CKEditor } from 'ckeditor4-react';
import Header from './Header';

function Post() {
  return (
    <div class="Post">
      <Header></Header>
        <div class = "post">
        <form>
            <div class="form-group row">
                <label for="staticEmail" class="col-sm-2 col-form-label">Title</label>
                <div class="col-sm-10">
                <input type="text" readonly class="form-control form-body" id="staticEmail" value="email@example.com"></input>
                </div>
            </div>

            <div class="form-group row">
                <label for="inputPassword" class="col-sm-2 col-form-label">Content</label>
                <div class="col-sm-20">
                <CKEditor></CKEditor>
                </div>
            </div>
        </form>

        <button class="btn btn-post bg-dark btn-outline-light btn-lg px-5 text-white ">Go to Question!</button>
        </div>
     </div>

  );
}

export default Post;
