import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { CKEditor } from 'ckeditor4-react';
import Axios from "axios";
import { useEffect, useState, } from 'react';
import { useParams } from 'react-router-dom';

function Detail() {

    const { idpost } = useParams();
    const [viewContent, setViewContent] = useState([]);
    const [answer, setAnswer] = useState([]);

    const onAnswerHandler = (e) => {
        setAnswer(e.target.value);
    }

    useEffect(()=>{
        Axios.get(`http://localhost:8000/detail/${idpost}`)
        .then((res)=>{
            console.log(res.data);
            setViewContent(res.data);
        })
    }, [idpost]);

    return (
        <div className="Detail">
            <div class = "body">
                
                <div class="box">
                    <div class="container question-container">
                    <h3>Question</h3> 

                    {viewContent.map(element =>
                        <div class="box-part">
                        <div class="title content-box">
                        <h5>{element.TITLE}</h5>
                        </div>
                        <div class="text content-box">
                        <span>{element.CONTENT}</span>
                        </div>  
                        <button class="btn btn-edit btn-outline-light btn-lg px-5 text-dark ">Edit</button>        
                    </div>
             
                  )}

                        
                    </div>

                    <div class="container answer-container">
                    <h3>Answer</h3>   
                        <div class="box-part">
                            <div class="text content-box">
                            <span>Lorem ipsum dolor sit amet, id quo eruditi eloquentiam. Assum decore te sed. Elitr scripta ocurreret qui ad.</span>
                            </div>    
                            <button class="btn btn-accept btn-outline-light btn-lg px-5 text-dark ">Accept</button>         
                        </div>

                        <div class="box-part">
                            <div class="text content-box">
                            <span>Lorem ipsum dolor sit amet, id quo eruditi eloquentiam. Assum decore te sed. Elitr scripta ocurreret qui ad.</span>
                            </div>    
                            <button class="btn btn-accept btn-outline-light btn-lg px-5 text-dark ">Accept</button>       
                        </div>

                        <div class="box-part">
                            <div class="text content-box">
                            <span>Lorem ipsum dolor sit amet, id quo eruditi eloquentiam. Assum decore te sed. Elitr scripta ocurreret qui ad.</span>
                            </div>       
                            <button class="btn btn-accept btn-outline-light btn-lg px-5 text-dark ">Accept</button>    
                        </div>

                    </div>

                    <div class="container answer-container">
                    <h3>What's your Answer?</h3>
                    <div class = "input-part">
                    <input type="text" readonly class="form-control form-body form-content" id="answer" name="answer"
                    value={answer} onChange={onAnswerHandler}></input>
                    </div>
                    
                    </div>
                    <button class="btn btn-answer bg-dark btn-outline-light btn-lg px-5 text-white ">Go to Question!</button>

                    
                </div>
            </div>
        </div>

  );
}

export default Detail;
