import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import Axios from "axios";

function Board() {

  const [viewContent, setViewContent] = useState([]);

  useEffect(()=>{
    Axios.get("http://localhost:8000/list")
    .then((res)=>{
      console.log(res.data);
      setViewContent(res.data);
    })
  }, [viewContent])

  return (
    <div className="Board">
      <div class = "body">

        <h2 class = "main-title">Today's Question</h2>

        <div class="box">
          <div class="container">
            <div class="row">

                <div class="col-lg-4 col-md-4 col-sm-4 col-xs-12">

                <div class="box-part text-center">  

                  {viewContent.map(element =>
                  <div>
                    <div class="title">
                      <h4>{element.TITLE}</h4>
                    </div>

                    <div class="text">
                      <span>{element.CONTENT}</span>
                    </div>

                    <Link to="/detail">Read More</Link>
                    </div>
                  )}


                </div>
              </div>	 



          </div>		
          </div>

          <nav aria-label="Page navigation example">
            <ul class="pagination justify-content-center">
              <li class="page-item disabled">
                <Link to="/" style={{ textDecoration: 'none' }}><div class="page-link">Previous</div></Link>
              </li>
              <li class="page-item"><Link to="/" style={{ textDecoration: 'none' }}><div class="page-link">1</div></Link></li>
              <li class="page-item"><Link to="/" style={{ textDecoration: 'none' }}><div class="page-link">2</div></Link></li>
              <li class="page-item"><Link to="/" style={{ textDecoration: 'none' }}><div class="page-link">3</div></Link></li>
              <li class="page-item">
                <Link to="/" style={{ textDecoration: 'none' }}><div class="page-link">Next</div></Link>
              </li>
            </ul>
          </nav>

        </div>



      </div>
    </div>
  );
}

export default Board;
