import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import React, {useState} from 'react'

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState(""); 
    
    const onUsernameHandler = (event) => {
      setUsername(event.currentTarget.value);
    }
  
    const onPasswordHandler = (event) => {
      setPassword(event.currentTarget.value)
  }
  
    const onSubmit = (event) => {
      event.preventDefault();
    }


  return (
    <div className="Login">
      <section class="vh-100 gradient-custom body">
        <div class="container py-5 h-100">
            <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-8 col-lg-6 col-xl-5">
                <div class="card bg-white text-black">
                <div class="card-body p-5 text-center">

                    <div class="mb-md-5 mt-md-4 pb-5">
                        <h2 class="fw-bold mb-2 text-uppercase">Login</h2>
                        <p class="text-gray-50 mb-5">Please enter your ID and password!</p>

                        <div class="form-outline form-white mb-4">
                            <input type="text" id="username" name="username" placeholder="Username" 
                            value={username} onChange={onUsernameHandler} class="form-control form-control-lg" />        
                        </div>

                        <div class="form-outline form-white mb-4">
                            <input type="password" id="password" name="password" placeholder="Password"     
                            value={password} onChange={onPasswordHandler} class="form-control form-control-lg" />             
                        </div>

                        <button  type="submit" onSubmit={onSubmit} class="btn bg-dark btn-outline-light btn-lg px-5">Login</button>

                    </div>

                   
                    <p class="mb-0">Don't have an account? <a href="#!" class="text-black">Register</a></p>
     
                </div>
                </div>
            </div>
            </div>
        </div>
        </section>
     </div>

  );
}

export default Login;
