import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { CKEditor } from 'ckeditor4-react';

import React, {useState} from 'react'
import { Link } from 'react-router-dom';
import Axios from "axios";


function Post() {

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");


  const onTitleHandler = (e) => {
      setTitle(e.target.value);
  }
  const onContentHandler = () => {
    const data = CKEditor.instances.contents.getData();
    setContent(data);
}

  const onPost = () => {

      const postInfo = {
          title,
          content
      }

      var hasEmptyInfo = false;

      for(var i in postInfo){
          if(!postInfo[i])
          hasEmptyInfo = true;
      }

      console.log(postInfo.title);
      console.log(postInfo.content);

      if(hasEmptyInfo){
          console.log('모든 항목을 다 입력해주십시오.');
      }

      else{
          console.log(postInfo);
          Axios.post("http://localhost:8000/post", postInfo)
          .then((res)=>{
              console.log(res);
          })
          .catch((e)=>{
              console.error(e);
          });
      };
  }

  return (
    <div class="Post">
        <div class = "post">
        <form>
            <div class="form-group row">
                <label for="title" class="col-sm-2 col-form-label">Title</label>
                <div class="col-sm-10">
                <input type="text" readonly class="form-control form-body" id="title" name="title"
                 value={title} onChange={onTitleHandler}></input>
                </div>
            </div>

            <div class="form-group row">
                <label for="content" class="col-sm-2 col-form-label">Content</label>
                <div class="col-sm-10">
                    <textarea class="ckeditor" col="80" row = "10" id="contents" name = "contents" 
                     onChange = {onContentHandler} />
                </div>
            </div>
        </form>

        <button onClick={onPost} class="btn btn-post bg-dark btn-outline-light btn-lg px-5 text-white ">Go to Question!</button>
        </div>
     </div>

  );
}

export default Post;
